import React, { useState} from "react";
import Input from "../Input/Input";
function TextField (rowData) {
   
    const { id, english, russian, transcription } = rowData;
    const [isSelected, setIsSelected] = useState(false);
    const [value, setValue] = useState({
      id:id,
      english:english,
      russian:russian,
      transcription:transcription,
    });
    
    function handleClose() {
        setIsSelected(!isSelected);
        setValue({ ...rowData });
      }
      function handleSave() {
        setValue({...value});
        setIsSelected(!isSelected);
      }
    
      function handleEdit() {
        setIsSelected(!isSelected);
      }
    
      function handleChange(event) {
        setValue((prevValue) => {
          return { ...prevValue, [event.target.english]: event.target.russian };
        });
      }

return  isSelected ? (
    <Input/>
):(
<section >
<div className='row'> 
   <div className ="cell">{english}</div>
   <div className ="cell"> {transcription}</div>
    <div className ="cell"> {russian}</div>
    <div className ="cell"><button className= 'btn' onClick={handleEdit}>Edit</button><button className= 'btn_save'>Save</button></div>
</div>
</section>
);
}
export default TextField
