import styles from "./translator.css"
import React, {useState} from "react";


export const  Translator = ({words}) => {
 const { id,english, russian} = words;
 const [isSelected, setIsSelected] = useState (false);
function Translator() {
    
    return (
        <container>
                       <div >
             {
    isSelected ? (
      <div className="translatorCard" onClick= {() => setIsSelected(false)}>
        {english}
      </div>
         ) : (
    <div  className="translatorCard" onClick={() => setIsSelected(true)}>
      
            {russian}
            </div> )}
            </div>
            </container>
  )}
}
