import React, {useState} from "react";
import data from "../.json"
let index;
 index= Math.floor(Math.random() * Object.keys(data).length);

export default Translator = ({words}) => {
 const { id, english, russian, transcription} = words;
 const [isSelected, setIsSelected] = useState (null);
 

    return (
        <container>
    <div >
             {
    isSelected ? (
      <div className="translatorCard" onClick= {() => setIsSelected(false)}>
        {words[index].english}
      </div>
         ) : (
    <div  className="translatorCard" onClick={() => setIsSelected(true)}>
      
            {words[index].russian}
            </div> )}
            </div>
            </container>
  )}
