import  styles from './card.css'
import Input from '../Input/Input';
import TextField from '../TextField/TextField';
import { useState } from "react";

function Card ({ word, handleDelete, handleSave }) {
    const {id, english, transcription, russian } = word;
    const [isClicked, btnIsClicked] = useState (false);
    const [value, setValue] = useState({
        id,
        english,
        transcription,
        russian,
    });

const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    setValue((prevWord) => {
        return { ...prevWord, [name]: value };
    });
};
    const handleCancel = () => {
        btnIsClicked(!isClicked);
        setValue({ ...word });
    };
    const updateWord = () => {
        handleSave(value);
        btnIsClicked(false);
    }

    const handleEdit = () => {
       btnIsClicked(!isClicked);
    }
    return (
        <tr>
            <td>{id}</td>
            {isClicked
                ? (
                    <>
                        <td>
                         
                            <input type="text"
                                className="input-field"
                                onChange={handleChange}
                                value={value.english}
                                name="english">
                            </input>
                        </td>
                        <td>
                            <input type="text"
                                className="input-field"
                                onChange={handleChange}
                                value={value.transcription}
                                name="transcription">
                            </input>
                        </td>
                        <td>
                            <input type="text"
                                className="input-field"
                                onChange={handleChange}
                                value={value.russian}
                                name="russian">
                            </input>
                        </td>
                    </>
                )
                : (
                    <>
                        <td>{value.english}</td>
                        <td>{value.transcription}</td>
                        <td>{value.russian}</td>
                    </>
                )}

            <td >
                {isClicked
                    ? (
                        <div>
                            <button
                                name={"Save"}
                                disabled={isDisabled}
                                onClick={updateWord}
                            //onClick={() => { setPressed(false); }}
                            />
                            <button
                                name={"Cancel"}
                                onClick={handleCancel} />
                        </div>)
                    : (
                        <div >
                            <bu
                                name={"Delete"}
                                onClick={() => handleDelete(id)} />
                            <TableButton
                                name={"Edit"}
                                onClick={handleEdit} />
                        </div>
                    )}
            </td>
        </tr>
    );
}
export default Card
